import './App.css';
import Message from './Message';
import SideBar from './SideBar';
import TextBar from './TextBar';
import { useEffect, useState } from 'react';
import {client} from './Contexts/GrpcContext'
import { User } from "./proto_js/chat_pb";
function App() {

  //need to get friends and inboxes/unread

  const [friends, setFriends] = useState([]);
  const [rooms, setRooms] = useState([]);
  const [username, setUsername] = useState('');
  const [userpfp, setUserPfp] = useState('');

  useEffect(() => {
    const user = new User();
    user.setUserid('test');
    client.userInfo(user, null, (err, response) => {
      console.log(err, response.toObject());
      if(response) {
        setFriends(response.userdata.friendsList);
        setRooms(response.userdata.roomsList);
        setUsername(response.username);
        setUserPfp(response.userpfp);
      }
    }); 

  }, []);


  return (
    <div className="App h-screen w-screen flex">
      <SideBar />
      <div className="w-full flex h-screen p-4">
        <Message userName="test_user_1" message="Tired"/>
        <TextBar />
      </div>
    </div>
  );
}

export default App;
